frappe.ui.form.on('Bank', {
	setup: function(frm) {
		frm.set_query("account", function() {
			return {
				filters: { account_type: 'Bank', is_group: 0}
			};
		});
	}
});

frappe.ui.form.on('Sales Invoice', {
	tfy_transaction_type: function(frm) {
	    if (frm.doc.tfy_transaction_type) {
		    frappe.call({
			    method: "tfy.custom_method.get_accounting_dimension_defaults",
			    args: {
				    transaction_type: frm.doc.tfy_transaction_type
			    }
		    }).done((r) => {
			    r.message.forEach((d) => {
				    frm.set_value(d.accounting_dimension_fieldname, d.default_value)
			    });
		    })
	    }
	},
	company_address: function(frm) { 
        frappe.call({ method: 'frappe.client.get_value', 
            args: { 
                'doctype': 'Dynamic Link', 'parent': 'Address', 
                'filters': { 'link_doctype': 'Warehouse', 'parent': frm.doc.company_address }, 
                'fieldname': 'link_name' 
            }, 
            callback: function(r) { 
                if(r.message) { 
                    frm.set_value('warehouse', r.message.link); 
                    $.each(frm.doc.items || [], function(i, item) { 
                        frappe.model.set_value(item.doctype, item.name, "warehouse", r.message.link_name); 
                    }); 
                } 
            } 
        }); 
    },
    validate: function(frm) {
         frappe.call({
	       method: 'frappe.client.get_value',
	       args: {
	           'doctype': 'Company',
	           'filters': {
	                'name': frm.doc.company,
	           },
	           'fieldname': 'inter_company_income_account'
	       },
	       callback: function(r) {
	           if(r.message && frm.doc.is_internal_supplier) {
	                 $.each(frm.doc.items || [], function(i, item) {
		                frappe.model.set_value(item.doctype, item.name, "income_account", r.message.inter_company_income_account);
	                 });
	           }
	       }
	   });
	}
});

