frappe.ui.form.on('Sales Order Item', {
	items_add: function(frm, cdt, cdn) {
		var row = frappe.get_doc(cdt, cdn);
		frm.script_manager.copy_from_first_row("items", row, 'warehouse');
	},
})

frappe.ui.form.on('Data Import Beta', {
    refresh(frm) {
        if (frm.doc.status == "Success") {
            frm.add_custom_button(__('Match'), function()  {
                frm.events.match_file(frm);                    
            }, __("Bank File"));               

            frm.add_custom_button(__('Create Entries'), function() {                        
                frm.events.create_entries(frm);  
            }, __("Bank File"));
        }            
    },
    match_file: function(frm) 
    { 
        frappe.call({
            method: "tfy.tfy.doctype.bank_file.bank_file.match_entries",        
            callback: function(r) {           
                console.log(r.message);        
            }    
        });
    },
    create_entries: function(frm) {    
        frappe.call({        
            method: "tfy.tfy.doctype.bank_file.bank_file.create_recon_entries",        
            callback: function(r) {           
                console.log(r.message);        
            }
        });
    }
});

